import React from 'react';
import { Modal, Button } from 'react-bootstrap';

export default function TodayTimeTable(props) {
  const calendar = props.timetable;
  console.log(calendar);
  const [show, setShow] = React.useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
      <Button id="viewWholeTimeTable" onClick={handleShow}>
        расписание целиком
      </Button>

      <Modal show={show} onHide={handleClose} animation={false}>
        <Modal.Header closeButton>
          <Modal.Title>Расписание на сегодня</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <table>
            {calendar.map((i, index) => {
              return (
                <tr key={index}>
                  <td>{calendar[index].start}</td>
                  <td>{calendar[index].name}</td>
                </tr>
              );
            })}
          </table>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Закрыть
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}
