import React, { Suspense } from 'react';
import axios from 'axios';
import { Col } from 'react-bootstrap';

const SingleLecRecord = React.lazy(() => import('./SingleLecRecord'));

export default class LectureRecords extends React.Component {
  constructor(props) {
    super(props);
    this.subject_id = this.props.subject_id;

    this.state = {
      lectories: [],
    };
  }

  componentDidMount() {
    const apiUrl = `/api/subjectlectories/?id=${this.subject_id}`;
    axios.get(apiUrl).then((resp) => {
      if (resp.data.length > 0) {
        this.setState({
          lectories: resp.data,
        });
      }
    });
  }

  render() {
    if (this.state.lectories) {
      return (
        <div className="page-subdirect__lectorBlock">
          <div className="wrapper">
            {this.state.lectories.map((res, index) => (
              <Suspense
                fallback={
                  <Col>
                    <div className="lec_title">{res.title}</div>
                    <div>Loading...</div>
                    <div className="info-block__date">
                      {moment(res.date).locale('ru').format('ll')}
                    </div>
                  </Col>
                }>
                <SingleLecRecord lec={res} key={index} />
              </Suspense>
            ))}
          </div>
        </div>
      );
    } else {
      return 'none';
    }
  }
}
